import com.eviware.soapui.support.XmlHolder

def vName=testRunner.testCase.testSuite.testCases["addEmployeeTC"].getPropertyValue("name")
def vRequest= testRunner.testCase.testSuite.testCases["addEmployeeTC"].testSteps["addEmployee"].getPropertyValue("Request")
def vXMLHolder=new XmlHolder(vRequest)

vXMLHolder.setNodeValue("//typ:addEmployee/typ:name",vName)
vXMLHolder.setNodeValue("//typ:addEmployee/typ:id","799")
vXMLHolder.setNodeValue("//typ:addEmployee/typ:Department","software testing")
vXMLHolder.setNodeValue("//typ:addEmployee/typ:age","25")

def vExec=vXMLHolder.getXml()
testRunner.testCase.testSuite.testCases["addEmployeeTC"].testSteps["addEmployee"].setPropertyValue("Request",vExec)



import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
def vReqExec=testRunner.testCase.testSuite.testCases["addEmployeeTC"].testSteps["addEmployee"]
def vContext=new WsdlTestRunContext(vReqExec)
vReqExec.run(testRunner,vContext)

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext


def vName=testRunner.testCase.testSuite.testCases["addEmployeeTC"].getPropertyValue("name")
def vOutReq= testRunner.testCase.testSuite.testCases["getEmployeeTC"].testSteps["getEmployeeDetails"].getPropertyValue("Request")
def vXMLHolder=new XmlHolder(vOutReq)
vXMLHolder.setNodeValue("//typ:employeeName",vName)
def vGetXML=vXMLHolder.getXml()

testRunner.testCase.testSuite.testCases["getEmployeeTC"].testSteps["getEmployeeDetails"].setPropertyValue("Request",vGetXML)
def vContext=testRunner.testCase.testSuite.testCases["getEmployeeTC"].testSteps["getEmployeeDetails"]
def vRun=new WsdlTestRunContext(vContext)
vContext.run(testRunner,vRun)

def vResponse=testRunner.testCase.testSuite.testCases["getEmployeeTC"].testSteps["getEmployeeDetails"].getPropertyValue("Response")
def vGetRes=new XmlHolder(vResponse)
def vNameRes=vGetRes.getNodeValue("//ns:name")


assert vNameRes==vName
